
static inline double eval_series(double* coeffs, double x, double b, int Ns)
{
  // Evaluate power series with terms
  // c(p)*(b/x)^2p
  //
  double f = 0.0;
  double bx2 = b*b/(x*x);
  double bx2p = 1.0;
  for (int p=0; p<Ns; p++)
    {
      bx2p = bx2p*bx2;
      f += bx2p*coeffs[p];
    }
  return f;  
}

